func TestEncode(t *testing.T) {
	ii := big.Int{}
	ii.SetString("123", 10)
	low := big.Int{}
	low.And(&ii, big.NewInt(int64(math.Pow(2.0, 64.0)-1)))
	high := big.Int{}
	high.Rsh(&ii, 64)
	iii := serde.Uint128{
		High: high.Uint64(),
		Low:  low.Uint64(),
	}
	bytes, _ := types.BcsSerializeU128(iii)
	t.Log("encoded : ", hex.EncodeToString(bytes))
}

